import React, { useState, useEffect } from "react";
import "./challenges.css";

const ProgressBar = ({ progress }) => {
    return (
        <div className="custom-progress-bar">
            <div className="custom-progress-fill" style={{ width: `${progress}%` }}>
                {progress}%
            </div>
        </div>
    );
};

const Challenges = () => {
    const [challenges, setChallenges] = useState([]);
    const [expGagnee, setExpGagnee] = useState(0);
    const [niveau, setNiveau] = useState(1);
    const [experience, setExperience] = useState(0);
    const [experiencePourNiveauSuivant, setExperiencePourNiveauSuivant] = useState(100);

    // Charger les challenges et la progression
    useEffect(() => {
        fetch("http://localhost:5000/api/challenges")
            .then((res) => res.json())
            .then((data) => setChallenges(data))
            .catch((err) => console.error("Erreur chargement challenges:", err));

        fetch("http://localhost:5000/api/progression")
            .then((response) => response.json())
            .then((data) => {
                const progression = data.utilisateur || data; // Récupérer correctement les données
                setNiveau(progression.niveau);
                setExperience(progression.experience);
                setExperiencePourNiveauSuivant(progression.experiencePourNiveauSuivant);
            })
            .catch((err) => console.error("Erreur lors du chargement de la progression :", err));
    }, []);

    // Mettre à jour le statut du challenge
    const handleValidate = (id) => {
        fetch(`http://localhost:5000/api/challenges/${id}`, {
            method: "PUT",
            headers: { "Content-Type": "application/json" },
            body: JSON.stringify({ validé: true }),
        })
            .then((res) => res.json())
            .then(({ utilisateur, expGagnee }) => {
                setChallenges(
                    challenges.map((ch) =>
                        ch.id === id ? { ...ch, validé: true } : ch
                    )
                );
                ajouterExperience(expGagnee);
                setExpGagnee(expGagnee);
                setTimeout(() => setExpGagnee(0), 3000);
                // Mettre à jour l'interface avec les nouvelles données utilisateur
                setNiveau(utilisateur.niveau);
                setExperience(utilisateur.experience);
                setExperiencePourNiveauSuivant(utilisateur.experiencePourNiveauSuivant);
            })
            .catch((err) => console.error("Erreur validation challenge:", err));
    };


    // Ajouter de l'expérience et sauvegarder
    const ajouterExperience = (exp) => {
        let nouvelleExperience = experience + exp;
        let nouveauNiveau = niveau;
        let nouvelleExpPourNiveauSuivant = experiencePourNiveauSuivant;

        while (nouvelleExperience >= nouvelleExpPourNiveauSuivant) {
            nouvelleExperience -= nouvelleExpPourNiveauSuivant;
            nouveauNiveau++;
            nouvelleExpPourNiveauSuivant = Math.floor(nouvelleExpPourNiveauSuivant * 1.5);
        }

        setNiveau(nouveauNiveau);
        setExperience(nouvelleExperience);
        setExperiencePourNiveauSuivant(nouvelleExpPourNiveauSuivant);

        // Sauvegarder la progression
        fetch("http://localhost:5000/api/progression", {
            method: "PUT",
            headers: { "Content-Type": "application/json" },
            body: JSON.stringify({
                niveau: nouveauNiveau,
                experience: nouvelleExperience,
                experiencePourNiveauSuivant: nouvelleExpPourNiveauSuivant
            }),
        }).catch((err) => console.error("Erreur sauvegarde progression:", err));
    };

    const progression = Math.floor((experience / experiencePourNiveauSuivant) * 100);

    return (
        <div className="challenges-container">
            <h2 className="color-white">Défis physiques</h2>

            {/* Affichage du niveau et barre de progression */}
            <div className="level-container">
                <h3 className="color-white">Niveau {niveau}</h3>
                <ProgressBar progress={progression} />
            </div>

            {expGagnee > 0 && (
                <div className="exp-gagnee">+{expGagnee} EXP gagnée 🎉</div>
            )}

            <table className="challenges-table">
                <thead>
                <tr>
                    <th>Nom du challenge</th>
                    <th>Description</th>
                    <th>EXP</th>
                    <th>Statut</th>
                    <th>Action</th>
                </tr>
                </thead>
                <tbody>
                {challenges.map((challenge) => (
                    <tr className="color-white" key={challenge.id}>
                        <td>{challenge.nom}</td>
                        <td>{challenge.description}</td>
                        <td>{challenge.exp}</td>
                        <td>{challenge.validé ? "Validé" : "Non validé"}</td>
                        <td>
                            {!challenge.validé && (
                                <button
                                    className="validate-button"
                                    onClick={() => handleValidate(challenge.id)}
                                >
                                    Validé
                                </button>
                            )}
                        </td>
                    </tr>
                ))}
                </tbody>
            </table>
        </div>
    );
};

export default Challenges;
