import React, { useState, useEffect } from "react";
import {
    LineChart, Line, XAxis, YAxis, Tooltip, CartesianGrid, ResponsiveContainer, BarChart, Bar
} from "recharts";

const Statistiques = () => {
    const [calendarData, setCalendarData] = useState([]);
    const [seancesParMois, setSeancesParMois] = useState([]);
    const [seancesJanvier, setSeancesJanvier] = useState([]);

    // Charger les données depuis le fichier JSON avec vérification des erreurs
    useEffect(() => {
        fetch("http://localhost:5000/api/calendar")
            .then((res) => {
                if (!res.ok) {
                    throw new Error(`HTTP error! Status: ${res.status}`);
                }
                return res.json();
            })
            .then((data) => {
                console.log("Données du calendrier :", data);
                setCalendarData(data);

                // Calculer les séances par mois
                const seancesParMoisCalc = data.reduce((acc, item) => {
                    const month = new Date(item.date).getMonth();
                    acc[month] = acc[month] ? acc[month] + 1 : 1;
                    return acc;
                }, Array(12).fill(0));

                // Formater les données pour le LineChart
                const formattedData = seancesParMoisCalc.map((count, index) => ({
                    mois: new Date(2025, index).toLocaleString("fr-FR", { month: "long" }),
                    totalSeances: count,
                }));

                setSeancesParMois(formattedData);

                // Calculer les occurrences des séances uniquement pour janvier (mois 0)
                const seancesEnJanvier = data.filter((item) => new Date(item.date).getMonth() === 0);
                const seancesCount = seancesEnJanvier.reduce((acc, item) => {
                    acc[item.nom] = (acc[item.nom] || 0) + 1;
                    return acc;
                }, {});

                // Formater les données pour le BarChart
                const formattedJanvierData = Object.keys(seancesCount).map((nom) => ({
                    nom,
                    total: seancesCount[nom],
                }));

                setSeancesJanvier(formattedJanvierData);
            })
            .catch((err) => console.error("Erreur lors du chargement des données :", err));
    }, []);

    return (
        <div className="seances-chart">
            {/* Graphique des séances par mois */}
            <h2 style={{ color: "white" }}>Nombre total de séances par mois</h2>
            <ResponsiveContainer width="100%" height={400}>
                <LineChart data={seancesParMois}>
                    <CartesianGrid strokeDasharray="3 3" />
                    <XAxis dataKey="mois" />
                    <YAxis />
                    <Tooltip />
                    <Line type="monotone" dataKey="totalSeances" stroke="#8884d8" strokeWidth={2} />
                </LineChart>
            </ResponsiveContainer>

            {/* Nouveau graphique des séances en janvier */}
            <h2 style={{ color: "white", marginTop: "40px" }}>Nombre de fois que chaque séance a été effectuée en janvier</h2>
            <ResponsiveContainer width="100%" height={400}>
                <BarChart data={seancesJanvier}>
                    <CartesianGrid strokeDasharray="3 3" />
                    <XAxis dataKey="nom" />
                    <YAxis />
                    <Tooltip />
                    <Bar dataKey="total" fill="#82ca9d" />
                </BarChart>
            </ResponsiveContainer>
        </div>
    );
};

export default Statistiques;
