import React, { useState, useEffect } from "react";
import "./challenges.css";

const Challenges = () => {
    const [challenges, setChallenges] = useState([]);

    // Charger les challenges au démarrage
    useEffect(() => {
        fetch("http://localhost:5000/api/challenges")
            .then((res) => res.json())
            .then((data) => setChallenges(data))
            .catch((err) => console.error("Erreur chargement challenges:", err));
    }, []);

    // Mettre à jour le statut du challenge (validé)
    const handleValidate = (id) => {
        fetch(`http://localhost:5000/api/challenges/${id}`, {
            method: "PUT",
            headers: { "Content-Type": "application/json" },
            body: JSON.stringify({ validé: true }),
        })
            .then((res) => res.json())
            .then((updatedChallenge) => {
                // Mettre à jour l'affichage des challenges
                setChallenges(
                    challenges.map((challenge) =>
                        challenge.id === updatedChallenge.id ? updatedChallenge : challenge
                    )
                );
            })
            .catch((err) => console.error("Erreur validation challenge:", err));
    };

    return (
        <div className="challenges-container">
            <h2>Défis physiques</h2>

            <table className="challenges-table">
                <thead>
                <tr>
                    <th>Nom du challenge</th>
                    <th>Description</th>
                    <th>Statut</th>
                    <th>Action</th>
                </tr>
                </thead>
                <tbody>
                {challenges.map((challenge) => (
                    <tr key={challenge.id}>
                        <td>{challenge.nom}</td>
                        <td>{challenge.description}</td>
                        <td>{challenge.validé ? "Validé" : "Non validé"}</td>
                        <td>
                            {!challenge.validé && (
                                <button className="validate-button" onClick={() => handleValidate(challenge.id)}>
                                    Validé
                                </button>
                            )}
                        </td>
                    </tr>
                ))}
                </tbody>
            </table>
        </div>
    );
};

export default Challenges;
