import React, { useState, useEffect } from "react";
import "./challenges.css";

const Challenges = () => {
    const [challenges, setChallenges] = useState([]);
    const [expGagnee, setExpGagnee] = useState(0);

    // Charger les challenges
    useEffect(() => {
        fetch("http://localhost:5000/api/challenges")
            .then((res) => res.json())
            .then((data) => setChallenges(data))
            .catch((err) => console.error("Erreur chargement challenges:", err));
    }, []);

    // Mettre √† jour le statut du challenge
    const handleValidate = (id) => {
        fetch(`http://localhost:5000/api/challenges/${id}`, {
            method: "PUT",
            headers: { "Content-Type": "application/json" },
            body: JSON.stringify({ valid√©: true }),
        })
            .then((res) => res.json())
            .then(({ challenge, expGagnee }) => {
                setChallenges(
                    challenges.map((ch) =>
                        ch.id === challenge.id ? challenge : ch
                    )
                );
                setExpGagnee(expGagnee);
                setTimeout(() => setExpGagnee(0), 3000); // Message dispara√Æt apr√®s 3 sec
            })
            .catch((err) => console.error("Erreur validation challenge:", err));
    };

    return (
        <div className="challenges-container">
            <h2 className="color-white">D√©fis physiques</h2>

            {expGagnee > 0 && (
                <div className="exp-gagnee">+{expGagnee} EXP gagn√©e üéâ</div>
            )}

            <table className="challenges-table">
                <thead>
                <tr>
                    <th>Nom du challenge</th>
                    <th>Description</th>
                    <th>EXP</th>
                    <th>Statut</th>
                    <th>Action</th>
                </tr>
                </thead>
                <tbody>
                {challenges.map((challenge) => (
                    <tr className="color-white" key={challenge.id}>
                        <td>{challenge.nom}</td>
                        <td>{challenge.description}</td>
                        <td>{challenge.exp}</td>
                        <td>{challenge.valid√© ? "Valid√©" : "Non valid√©"}</td>
                        <td>
                            {!challenge.valid√© && (
                                <button
                                    className="validate-button"
                                    onClick={() => handleValidate(challenge.id)}
                                >
                                    Valid√©
                                </button>
                            )}
                        </td>
                    </tr>
                ))}
                </tbody>
            </table>
        </div>
    );
};

export default Challenges;
