import React, { useState, useEffect } from "react";
import Select from "react-select";
import chroma from "chroma-js";
import "./calendar.css";

const dot = (color = "transparent") => ({
    alignItems: "center",
    display: "flex",
    ":before": {
        backgroundColor: color,
        borderRadius: 10,
        content: '" "',
        display: "block",
        marginRight: 8,
        height: 10,
        width: 10,
    },
});

const colourStyles = {
    control: (styles) => ({ ...styles, backgroundColor: "white" }),
    option: (styles, { data, isDisabled, isFocused, isSelected }) => {
        const color = chroma(data.couleur);
        return {
            ...styles,
            backgroundColor: isDisabled
                ? undefined
                : isSelected
                    ? data.couleur
                    : isFocused
                        ? color.alpha(0.1).css()
                        : undefined,
            color: isDisabled
                ? "#ccc"
                : isSelected
                    ? chroma.contrast(color, "white") > 2
                        ? "white"
                        : "black"
                    : data.couleur,
            cursor: isDisabled ? "not-allowed" : "default",
        };
    },
    singleValue: (styles, { data }) => ({ ...styles, ...dot(data.couleur) }),
};

const Calendar = () => {
    const [currentMonth, setCurrentMonth] = useState(0);
    const [trackedDays, setTrackedDays] = useState({});
    const [seances, setSeances] = useState([]);
    const [showPopup, setShowPopup] = useState(false);
    const [selectedDay, setSelectedDay] = useState(null);

    // Charger les séances depuis le fichier JSON
    useEffect(() => {
        fetch("http://localhost:5000/api/seances")
            .then((res) => res.json())
            .then((data) => {
                console.log("Séances récupérées:", data);  // Vérifie dans la console
                setSeances(data);
            })
            .catch((err) => console.error("Erreur lors du chargement des séances:", err));
    }, []);

    // Sauvegarder les trackedDays dans le localStorage
    useEffect(() => {
        fetch("http://localhost:5000/api/calendar")
            .then((res) => res.json())
            .then((data) => {
                const tracked = {};
                data.forEach((entry) => {
                    const entryDate = new Date(entry.date);
                    // Filtrer les séances correspondant au mois en cours
                    if (entryDate.getMonth() === currentMonth) {
                        const day = entryDate.getDate();
                        tracked[day] = entry.color;
                    }
                });
                setTrackedDays(tracked);
            })
            .catch((err) => console.error("Erreur lors du chargement des données du calendrier:", err));
    }, [currentMonth]);



    const handleDayClick = (day) => {
        setSelectedDay(day);
        setShowPopup(true);
    };

    const handleSeanceSelect = (selectedOption) => {
        // Créer la date complète au format 'YYYY-MM-DD' en utilisant UTC
        const selectedDate = new Date(Date.UTC(2025, currentMonth, selectedDay)); // Utilisation de Date.UTC() pour éviter le décalage de fuseau horaire
        const formattedDate = selectedDate.toISOString().split('T')[0];  // Format 'YYYY-MM-DD'

        const newEntry = {
            date: formattedDate,
            color: selectedOption.couleur,
        };

        fetch("http://localhost:5000/api/calendar", {
            method: "POST",
            headers: {
                "Content-Type": "application/json",
            },
            body: JSON.stringify({
                date: "2025-01-05", // La date sélectionnée
                seanceId: 1, // L'ID de la séance sélectionnée
                color: "#ff5733", // Optionnel, juste pour l'affichage
            }),
        })
            .then((res) => {
                if (!res.ok) throw new Error(`HTTP error! Status: ${res.status}`);
                return res.json();
            })
            .then((data) => {
                console.log("Séance enregistrée avec succès :", data);
            })
            .catch((err) => console.error("Erreur lors de l'enregistrement :", err));
    }




        const monthNames = [
        "Janvier", "Février", "Mars", "Avril", "Mai", "Juin",
        "Juillet", "Août", "Septembre", "Octobre", "Novembre", "Décembre"
    ];

    const daysInMonth = new Date(2025, currentMonth + 1, 0).getDate();
    const firstDayOfMonth = new Date(2025, currentMonth, 1).getDay();
    const calendarDays = Array(firstDayOfMonth).fill(null).concat(
        Array.from({ length: daysInMonth }, (_, i) => i + 1)
    );

    return (
        <div className="calendar-container">
            <div className="calendar-header">
                <button onClick={() => setCurrentMonth((prev) => (prev - 1 + 12) % 12)}>
                    <ion-icon name="arrow-back-outline"></ion-icon>
                </button>
                <h1 className="calendar-title">{monthNames[currentMonth]} 2025</h1>
                <button onClick={() => setCurrentMonth((prev) => (prev + 1) % 12)}>
                    <ion-icon name="arrow-forward-outline"></ion-icon>
                </button>
            </div>
            <div className="calendar-grid">
                {["Dim", "Lun", "Mar", "Mer", "Jeu", "Ven", "Sam"].map((dayName) => (
                    <div key={dayName} className="calendar-day-name">{dayName}</div>
                ))}
                {calendarDays.map((day, index) => (
                    <div
                        key={index}
                        onClick={() => day && handleDayClick(day)}
                        className={`calendar-day ${day ? "clickable" : "empty"}`}
                        style={{
                            backgroundColor: day && trackedDays[day] ? trackedDays[day] : "white",
                        }}
                    >
                        {day && (
                            <span className={trackedDays[day] ? "day-number colored" : "day-number"}>
                                {day}
                            </span>
                        )}
                    </div>
                ))}
            </div>
            {showPopup && (
                <div className="popup-overlay">
                    <div className="popup">
                        <h3>Choisir une séance</h3>
                        <Select
                            options={seances.map((seance) => ({
                                value: seance.id,
                                label: seance.nom,
                                couleur: seance.couleur,
                            }))}
                            styles={colourStyles}
                            onChange={handleSeanceSelect}
                        />
                        <div className="popup-buttons">
                            <button
                                type="button"
                                className="close-button"
                                onClick={() => setShowPopup(false)}
                            >
                                Fermer
                            </button>
                            <button
                                type="button"
                                className="validate-button"
                                onClick={() => {
                                    // Logique de validation de la sélection
                                    setShowPopup(false);
                                }}
                            >
                                Valider
                            </button>
                        </div>
                    </div>
                </div>
            )}
        </div>
    );
};

export default Calendar;
