import React, { useState, useEffect } from "react";
import { LineChart, Line, XAxis, YAxis, Tooltip, CartesianGrid, ResponsiveContainer } from "recharts";

const Statistiques = () => {
    const [calendarData, setCalendarData] = useState([]);
    const [seancesParMois, setSeancesParMois] = useState([]);

    // Charger les données depuis le fichier JSON avec vérification des erreurs
    useEffect(() => {
        fetch("http://localhost:5000/api/calendar") // ✅ Nouvelle URL correcte
            .then((res) => {
                if (!res.ok) {
                    throw new Error(`HTTP error! Status: ${res.status}`);
                }
                return res.json();
            })
            .then((data) => {
                console.log("Données du calendrier :", data); // Vérification des données
                setCalendarData(data);

                // Calculer les séances par mois
                const seancesParMoisCalc = data.reduce((acc, item) => {
                    const month = new Date(item.date).getMonth();
                    acc[month] = acc[month] ? acc[month] + 1 : 1;
                    return acc;
                }, Array(12).fill(0));

                // Transformer en format utilisable par Recharts
                const formattedData = seancesParMoisCalc.map((count, index) => ({
                    mois: new Date(2025, index).toLocaleString("fr-FR", { month: "long" }),
                    totalSeances: count,
                }));

                console.log("Séances par mois :", formattedData); // Vérification des données pour le graphe
                setSeancesParMois(formattedData);
            })
            .catch((err) => console.error("Erreur lors du chargement des données :", err));
    }, []);

    return (
        <div className="seances-chart">
            <h2 style={{ color: "white" }}>Nombre total de séances par mois</h2>
            <ResponsiveContainer width="100%" height={400}>
                <LineChart data={seancesParMois}>
                    <CartesianGrid strokeDasharray="3 3" />
                    <XAxis dataKey="mois" />
                    <YAxis />
                    <Tooltip />
                    <Line type="monotone" dataKey="totalSeances" stroke="#8884d8" strokeWidth={2} />
                </LineChart>
            </ResponsiveContainer>
        </div>
    );
};

export default Statistiques;
